export function ToggleButtons(props){
    return (
        <div className="flex space-x-2">
          <button style = {{height : "5%"}}
            className={`bg-blue-500 text-white font-bold py-2 px-4 rounded ${
              props.mode === "summary" ? "bg-blue-700" : ""
            }`}
            onClick={props.onSum}
          >
            Summary
          </button>
          <button style = {{height : "5%"}}
            className={`bg-gray-400 text-black font-bold py-2 px-4 rounded ${
              props.mode === "ppt" ? "bg-gray-700" : ""
            }`}
            onClick={props.onPpt}
          >
            PowerPoint
          </button>
          <div class="mb-2"><button class="px-5 py-3 rounded-xl text-sm font-medium text-indigo-600 bg-white outline-none focus:outline-none m-1 hover:m-0 focus:m-0 border border-indigo-600 hover:border-4 focus:border-4 hover:border-indigo-800 hover:text-indigo-800 focus:border-purple-200 active:border-grey-900 active:text-grey-900 transition-all"><i class="mdi mdi-circle-outline mr-2 text-xl align-middle leading-none"></i>Button</button></div>
        </div>
      );
}
