export function ToggleButtons(props){
    return (
        <div className="">
          <button style = {{position: "absolute", height : "5%", left : "39%"}}
            className={`bg-gray-800 text-white font-thin py-2 px-4 rounded ${
              props.mode === "summary" ? "bg-blue-700" : ""
            }`}
            onClick={props.onSum}
          >
            Summary
          </button>
          
          <button style = {{position: "absolute", height : "5%", left : "54%"}}
            className={`bg-black text-white font-thin py-2 px-4 rounded ${
              props.mode === "info" ? "bg-yellow-700" : ""
            }`}
            onClick={props.onInfo}
          >
            Infographic
          </button>
      <button style = {{position: "absolute", height : "5%", left : "46%"}}
            className={`bg-black text-white font-thin py-2 px-4 ${
              props.mode === "ppt" ? "bg-green-800" : ""
            }`}
            onClick={props.onPpt}
          >
            PowerPoint
          </button>
        </div>
      );
}
