export function Description(){
    const [word,setWord] = "Summaries"
    const loopWord = () =>{
        if (word === "Summaries"){
            setWord("Infographics");
        }
        else if (word === "Infographics"){
            setWord("Powerpoints");
        }
        else setWord("Summaries");
    }
    return(
        <h1 className="mb-2 font-mono text-4xl text-gray-100 md:text-6xl">
        hi, I&apos;m <br className="block md:hidden" />
        <span className="relative">
        <span className="h-20 pt-2 overflow-x-hidden whitespace-nowrap text-brand-accent">
        {word} <span className="text-3xl md:text-5xl"></span>
        </span>
        <span
        className="{`${styles.cursor} absolute -bottom-0 left-0 -top-1 inline-block bg-black w-full animate-type will-change`}"
        onAnimationEnd={() => loopWord()}
        ></span>
    </span>
</h1>
    )
}
