export function ToggleButtons(props){
    return (
        <div className="flex space-x-2">
          <button style = {{height : "5%"}}
            className={`bg-blue-500 text-white font-bold py-2 px-4 rounded ${
              props.mode === "summary" ? "bg-blue-700" : ""
            }`}
            onClick={props.onSum}
          >
            Summary
          </button>
          <button style = {{height : "5%"}}
            className={`bg-gray-400 text-black font-bold py-2 px-4 rounded ${
              props.mode === "ppt" ? "bg-gray-700" : ""
            }`}
            onClick={props.onPpt}
          >
            PowerPoint
          </button>
          <button style = {{height : "5%", width : "44%"}}
            className={`"inline-flex items-center justify-center w-8 h-8 mr-2 text-pink-100 transition-colors duration-150 bg-pink-700 rounded-full focus:shadow-outline hover:bg-pink-800${
              props.mode === "info" ? "bg-yellow-700" : ""
            }`}
            onClick={props.onInfo}
          >
            Infographic
          </button>
        </div>
      );
}
